use std::collections::BTreeMap; use crate::core::address::Address; #[derive(Clone,Debug)] pub struct ChoiceF64{ pub addr:Address, pub value:f64, pub logp:f64 } #[derive(Clone,Debug,Default)] pub struct Trace{ pub choices:BTreeMap<Address,ChoiceF64>, pub log_prior:f64, pub log_likelihood:f64, pub log_factors:f64 } impl Trace{ pub fn total_log_weight(&self)->f64{ self.log_prior + self.log_likelihood + self.log_factors }}
